mapper.select:
	select stu_name, phone,school,major,msg_from,create_date from cv_info 
	
	    
	      stu_name = #{stuName}
	    
	    
	      AND phone = #{phone}

select t1.t_id tId, t1.stu_name stuName, t3.login_name userName, t3.t_id userId, t1.create_date createDate, t2.t_id relId, t2.visit_date visitDate, t2.pre_pay_date prePayDate, t2.pay_date payDate, t2.pay_type payType, t2.pay, t1.major, t1.phone, t1.education, t1.job, t1.msg_from msgFrom, t1.school, t1.intention from cv_info t1 inner join user_cv_rel t2 on t1.t_id = t2.stu_id inner join user_info t3 on t3.t_id = t2.user_id
order by t1.create_date DESC
limit #{offset}, #{limit}

SELECT t1.t_id stuId, t2.invite_id invitId, (select t3.login_name from user_info t3 where t3.t_id = t2.invite_id) invitPerson, t1.stu_name stuName , t1.create_date createDate, t1.phone, t1.sex, t1.school, t1.education, t1.major, t1.job, t1.msg_from msgFrom, t1.td_type tdType, t1.intention, t2.user_id userId, (select t3.login_name from user_info t3 where t3.t_id = t2.user_id) askPerson, t2.t_id relId from cv_info t1 left join user_cv_rel t2 on t1.t_id = t2.stu_id
and t2.invite_id = (select t3.t_id from user_info t3 where t3.login_name = #{param.login_name})
and t1.stu_name = #{param.stu_name}
and t1.create_date >= #{param.from_date}
and t1.create_date <= #{param.to_date}
and t2.invite_id = #{inviteId}
order by t1.create_date desc limit #{offset}, #{limit}

 	SELECT 
	    count(*)
	from cv_info t1 left join user_cv_rel t2
	on t1.t_id = t2.stu_id
	
		
			
				and t2.invite_id = (select t3.t_id from user_info t3 where t3.login_name = #{param.login_name})
			
			
